export function LogoWithName({ 
  logoSize = 32, 
  textSize = 'md', 
  className = '',
  gap = 'md' 
}: LogoWithNameProps) {
  const textSizeClasses = {
    sm: 'text-lg',
    md: 'text-xl',
    lg: 'text-2xl',
    xl: 'text-3xl'
  };

  const gapClasses = {
    sm: 'gap-2',
    md: 'gap-3',
    lg: 'gap-4'
  };

  return (
    <div className={`flex items-center ${gapClasses[gap]} ${className}`}>
      <Logo width={logoSize} height={logoSize} />
      <span 
        className={`font-semibold tracking-tight ${textSizeClasses[textSize]}`}
        style={{ fontFamily: '"League Spartan", sans-serif' }}
      >
        Mindscribe
      </span>
    </div>
  );
}
